import React, { useEffect, useState } from "react";
import axios from "axios";

const GenerateCat = props => {
  const [user, setUser] = useState({});
  const [breed, setBreed] = useState([]);

  const headers = {
    'x-api-key': '80ad793a-81c0-4987-a01a-cdf448c1b68d'
  }

  useEffect(() => {
    axios.get("http://battlecat.stark.mmi-unistra.fr/users/" + sessionStorage.getItem("userConnected")).then(response => {
      axios.put("http://battlecat.stark.mmi-unistra.fr/users/" + sessionStorage.getItem("userConnected"), {points: response.data[0].points - 10});
      response.data[0].points -= 10;
      setUser(response.data[0]);
    });

    axios.get("https://api.thecatapi.com/v1/breeds", {headers}).then(response => {
      const breed = response.data[Math.floor(Math.random() * response.data.length)];

      console.log(breed);
      const cat = {
        breed: breed.id,
        name: "",
        image_url: breed.url,
        strength: breed.weight.metric,
        hp_max: "",
        in_team: "",
        original_user_id: "",
        current_user_id: ""
      }
    });
  }, []);


  return (
    <div>
        <h1>Nouveau chat</h1>
        <p>Vous avez {user.points} points.</p>
    </div>
  );
};


export default GenerateCat;
