import React, { useEffect, useState } from "react";
import axios from "axios";

const GenerateCat = props => {
  const [user, setUser] = useState({});

  useEffect(() => {
    axios.get("http://battlecat.stark.mmi-unistra.fr/users/" + sessionStorage.getItem("userConnected")).then(response => {
      setUser(response.data[0]);
    });

    axios.put("http://battlecat.stark.mmi-unistra.fr/users/" + sessionStorage.getItem("userConnected"), {points: user.points - 10}).then(response => {
    });
  }, []);
  console.log(user);

  return (
    <div>
        <h1>Nouveau chat</h1>
        <p>Vous avez {user.points} points.</p>
        <a className="ghostButton" href="/generate_cat">Invoquer un chat ? (10 points)</a>
    </div>
  );
};


export default GenerateCat;
