              document.querySelector("#enemyHP").textContent(enemyCat.hp_max + " HP");
            break;
          case "enemyCat":
              damages = Math.floor(Math.random() * enemyCat.strength);
              userCat.hp_max -= damages;
              attackingCat = "userCat";
              document.querySelector("#userHP").textContent(userCat.hp_max + " HP");
          break;

          default:
            break;
        }
        console.log(attackingCat);
        console.log(damages);
        console.log(enemyCat.hp_max);
        console.log(userCat.hp_max);

