        </div>
      )
    } else {
      return (
        <h2>Fight !!!</h2>
      )
    }
  }

  return (
    <div id="fightPage">
      <DisplayResult />
      <div id="battleground">
        <div id="userSide">
          <h3>{props.location.fightProps.userCat[0].name}</h3>
          <img src={props.location.fightProps.userCat[0].image_url} alt=""></img>
          <p id="userHP">{props.location.fightProps.userCat[0].hp_max} HP</p>
          <p id="userAction"></p>
        </div>
        <div id="enemySide">
          <h3>{props.location.fightProps.enemyCat.breed}</h3>
          <img src={props.location.fightProps.userCat[0].image_url} alt=""></img>
          <p id="enemyHP">{props.location.fightProps.enemyCat.hp_max} HP</p>
          <p id="enemyAction"></p>
        </div>
      </div>

      <Link className="ghostButton" to="/search_cat">Retour</Link>
    </div>


  );
};

export default Fight;
