import React, { useEffect, useState } from "react";
import axios from "axios";
import { Switch, Route, Redirect, Link } from 'react-router-dom';

import CatCard from "../items/CatCard.js"


const WildCat = props => {
  const [userCat, setUserCat] = useState([]);
  const [breed, setBreed] = useState({});


  const headers = {
    'x-api-key': '80ad793a-81c0-4987-a01a-cdf448c1b68d'
  }

  useEffect(() => {
    axios.get("http://battlecat.stark.mmi-unistra.fr/cat/" + props.match.params.cat).then(response => {
      setUserCat(response.data);
    });

    axios.get("https://api.thecatapi.com/v1/breeds", {headers}).then(response => {
      setBreed(response.data[Math.floor(Math.random() * response.data.length)]);
    });
  }, []);


  return (
    <div>
        <h1>Choisissez quel chat vous voulez prendre avec vous</h1>

    </div>
  );
};

export default WildCat;
